from typing import List
from langchain.retrievers import BM25Retriever, EnsembleRetriever
from langchain.schema import Document, BaseRetriever
from langchain.vectorstores.base import VectorStore

from genflow import CustomComponent


class EnsembleRetrieverComponent(CustomComponent):
    display_name: str = "Ensemble Retriever"
    description: str = "The EnsembleRetriever takes a list of retrievers as input."
    documentation: str = (
        "https://python.langchain.com/docs/modules/data_connection/retrievers/ensemble"
    )
    beta = False

    def build_config(self):
        return {
            "documents": {"display_name": "Documents"},
            "retrievers": {
                "display_name": "Retrievers",
            },
            "top_k": {
                "display_name": "Top K",
                "is_list": False,
                "required": True,
                "value": 2,
                "info": "The number of results to return.",
            },
            "weights": {
                "display_name": "Weights",
                "is_list": False,
                "required": True,
                "value": "0.5, 0.5",
            },
            "code": {"show": False},
        }

    def build(
        self,
        top_k: int,
        weights: str,
        documents: List[Document],
        retrievers: List[VectorStore],
    ) -> BaseRetriever:
        docs = [document.page_content for document in documents]
        bm25_retriever = BM25Retriever.from_texts(docs)
        bm25_retriever.k = top_k

        _retrievers = [
            _retriever.as_retriever(search_kwargs={"k": top_k})
            for _retriever in retrievers
        ]
        _retrievers.append(bm25_retriever)

        _weights = [
            int(_weight) if isinstance(_weight, int) else float(_weight)
            for _weight in weights.split(",")
        ]

        return EnsembleRetriever(retrievers=_retrievers, weights=_weights)

from genflow import CustomComponent
from langchain.embeddings.base import Embeddings
from langchain.embeddings import HuggingFaceInferenceAPIEmbeddings


class HuggingFaceInferenceAPIEmbeddingsComponent(CustomComponent):
    display_name: str = "HuggingFaceInferenceAPI Embeddings"
    description: str = """Access HuggingFaceEmbedding model via inference api,download models locally."""
    documentation: str = "https://docs.aiplanet.com/components/embeddings#huggingface-inference-api-embeddings"
    beta = False

    def build_config(self):
        return {
            "inference_api_key": {
                "display_name": "Inference API Key",
                "is_list": False,
                "required": True,
                "value": "",
            },
            "model_name": {
                "display_name": "Model Name",
                "is_list": False,
                "required": True,
                "value": "",
            },
            "code": {"show": False},
        }

    def build(self, inference_api_key: str, model_name: str) -> Embeddings:
        return HuggingFaceInferenceAPIEmbeddings(
            api_key=inference_api_key, model_name=model_name
        )
