from genflow import CustomComponent
from langchain.embeddings.base import Embeddings
from langchain.embeddings import HuggingFaceInferenceAPIEmbeddings


class HuggingFaceInferenceAPIEmbeddingsComponent(CustomComponent):
    display_name: str = "HuggingFaceInferenceAPI Embeddings"
    description: str = """Access HuggingFaceEmbedding model via inference api, 
which does not require to install sentence_transformers 
and download models locally."""
    documentation: str = (
        "https://python.langchain.com/docs/integrations/text_embedding/huggingfacehub"
    )
    beta = False

    def build_config(self):
        return {
            "inference_api_key": {
                "display_name": "Inference API Key",
                "is_list": False,
                "required": True,
                "value": "",
            },
            "model_name": {
                "display_name": "Model Name",
                "is_list": False,
                "required": True,
                "value": "",
            },
            "code": {"show": False},
        }

    def build(self, inference_api_key: str, model_name: str) -> Embeddings:
        return HuggingFaceInferenceAPIEmbeddings(
            api_key=inference_api_key, model_name=model_name
        )

from typing import Optional
from genflow.interface.custom import CustomComponent
from langchain.llms.base import BaseLLM
from langchain.chat_models import AzureChatOpenAI


class AzureChatOpenAILLM(CustomComponent):
    display_name: str = "AzureChatOpenAI"
    description: str = "Azure Chat Open AI Chat&Completion large language models."

    AZURE_OPENAI_MODELS = [
        "gpt-4",
        "gpt-4-32k",
        "gpt-4-vision",
    ]

    def build_config(self):
        return {
            "model": {
                "display_name": "Model Name",
                "value": "gpt-4",
                "options": self.AZURE_OPENAI_MODELS,
                "required": True,
            },
            "api_key": {
                "display_name": "AzureChatOpenAI API Key",
                "required": True,
                "password": True,
            },
            "api_base": {
                "display_name": "AzureChatOpenAI API Base",
                "required": True,
            },
            "api_type": {"display_name": "AzureChatOpenAI API Type", "required": True},
            "azure_deployment": {
                "display_name": "Deployment Name",
                "required": True,
            },
            "api_version": {
                "display_name": "API Version",
                "value": "2023-07-01-preview",
                "required": True,
                "advanced": True,
            },
            "temperature": {
                "display_name": "Temperature",
                "value": 0.5,
                "field_type": "float",
                "required": False,
            },
            "max_tokens": {
                "display_name": "Max Tokens",
                "value": 512,
                "required": False,
                "field_type": "int",
                "advanced": True,
            },
            "code": {"show": False},
        }

    def build(
        self,
        model: str,
        api_base: str,
        api_type: str,
        api_key: str,
        azure_deployment: str,
        api_version: str = "2023-05-15",
        temperature: Optional[float] = 0.7,
        max_tokens: Optional[int] = 512,
    ) -> BaseLLM:
        try:
            output = AzureChatOpenAI(
                model_name=model,
                openai_api_base=api_base,
                openai_api_type=api_type,
                openai_api_key=api_key,
                openai_api_version=api_version,
                deployment_name=azure_deployment,
                temperature=temperature,
                max_tokens=max_tokens,
            )
        except Exception as e:
            raise ValueError("Could not connect to Azure ChatOpenAI model.") from e
        return output
