from typing import List
from genflow.interface.custom.custom_component import CustomComponent
from langchain_community.document_loaders import DropboxLoader
from langchain.schema import Document

class DropboxLoaderComponent(CustomComponent):
    display_name: str = "Dropbox Loader"
    description: str = "Dropbox is a file hosting service that brings everything-traditional files, cloud content, and web shortcuts together in one place. Recursively loads all the text files in all your folders in your dropbox."
    documentation: str = "https://docs.aiplanet.com/components/document-loaders#dropbox-loader"

    beta = False

    def build_config(self):
        return {
            "dropbox_access_token": {
                "display_name": "Dropbox Access Token",
                "required": True, 
                "password": True, 
                "input_types":["Input"]},
            "dropbox_folder_path": {
                "display_name": "Dropbox Folder Path",
                "required": False,
                "value": "",
            },
            "code": {"show": False},
        }

    def build(self, dropbox_access_token: str, dropbox_folder_path: str) -> List[Document]:
        loader = DropboxLoader(
            dropbox_access_token=dropbox_access_token, dropbox_folder_path=dropbox_folder_path, recursive=True
        )
        results = loader.load()
        return results
