from typing import Optional
from genflow.interface.custom import CustomComponent
from langchain.llms.base import BaseLLM
from langchain.chat_models import AzureChatOpenAI


class AzureChatOpenAILLM(CustomComponent):
    display_name: str = "AzureChatOpenAI"
    description: str = "Azure Chat Open AI Chat&Completion large language models."

    AZURE_OPENAI_MODELS = [
        "gpt-4",
        "gpt-4-32k",
        "gpt-4-vision",
    ]

    def build_config(self):
        return {
            "model": {
                "display_name": "Model Name",
                "value": "gpt-4",
                "options": self.AZURE_OPENAI_MODELS,
                "required": True,
            },
            "api_key": {
                "display_name": "AzureChatOpenAI API Key",
                "required": True,
                "password": True,
            },
            "api_base": {
                "display_name": "AzureChatOpenAI API Base",
                "required": True,
            },
            "api_type": {"display_name": "AzureChatOpenAI API Type", "required": True},
            "azure_deployment": {
                "display_name": "Deployment Name",
                "required": True,
            },
            "api_version": {
                "display_name": "API Version",
                "value": "2023-07-01-preview",
                "required": True,
                "advanced": True,
            },
            "temperature": {
                "display_name": "Temperature",
                "value": 0.5,
                "field_type": "float",
                "required": False,
            },
            "max_tokens": {
                "display_name": "Max Tokens",
                "value": 512,
                "required": False,
                "field_type": "int",
                "advanced": True,
            },
            "code": {"show": False},
        }

    def build(
        self,
        model: str,
        api_base: str,
        api_type: str,
        api_key: str,
        azure_deployment: str,
        api_version: str = "2023-05-15",
        temperature: Optional[float] = 0.7,
        max_tokens: Optional[int] = 512,
    ) -> BaseLLM:
        try:
            output = AzureChatOpenAI(
                model_name=model,
                openai_api_base=api_base,
                openai_api_type=api_type,
                openai_api_key=api_key,
                openai_api_version=api_version,
                deployment_name=azure_deployment,
                temperature=temperature,
                max_tokens=max_tokens,
            )
        except Exception as e:
            raise ValueError("Could not connect to Azure ChatOpenAI model.") from e
        return output

from typing import Optional, Union
from langchain.memory.chat_memory import BaseMemory, BaseChatMemory
from langchain.memory.buffer import ConversationBufferMemory
from langchain.memory.chat_message_histories.postgres import PostgresChatMessageHistory

from genflow import CustomComponent


class ConversationBufferMemoryComponent(CustomComponent):
    display_name: str = "ConversationBufferMemory"
    description: str = "Buffer for storing conversation memory."
    documentation: str = (
        "https://docs.aiplanet.com/components/memories#conversationbuffermemory"
    )
    beta = False

    def build_config(self):
        return {
            "input_key": {
                "display_name": "Input Key",
                "required": False,
                "value": "",
                "info": "The variable to be used as Chat Input when more than one variable is available.",
            },
            "memory_key": {
                "display_name": "Memory Key",
                "required": False,
                "value": "history",
            },
            "output_key": {
                "display_name": "Output Key",
                "required": False,
                "value": "",
                "info": "The variable to be used as Chat Output (e.g. answer in a ConversationalRetrievalChain)",
            },
            "return_messages": {
                "display_name": "Return Messages",
                "field_type": "bool",
                "required": False,
                "value": True,
            },
            "session_id": {
                "display_name": "Session ID",
                "required": False,
                "advanced": True,
                "value": "genflow_memory_db",
            },
            "connection_string": {
                "display_name": "Connection String",
                "required": False,
                "advanced": True,
                "value": "",
            },
            "code": {"show": False},
        }

    def build(
        self,
        input_key: Optional[str] = "",
        memory_key: Optional[str] = "history",
        output_key: Optional[str] = "",
        return_messages: Optional[bool] = True,
        session_id: Optional[str] = "genflow_memory_db",
        connection_string: Optional[str] = "",
    ) -> Union[BaseMemory, BaseChatMemory]:
        chat_memory = PostgresChatMessageHistory(
            session_id=session_id, connection_string=connection_string
        )

        keys = {}

        if input_key and input_key != "":
            keys["input_key"] = input_key

        if output_key and output_key != "":
            keys["output_key"] = output_key

        return ConversationBufferMemory(
            chat_memory=chat_memory,
            memory_key=memory_key,
            return_messages=return_messages,
            **keys
        )

from typing import Optional

from langchain.chains import LLMChain

from genflow import CustomComponent
from genflow.field_typing import (
    BaseLanguageModel,
    BaseMemory,
    BasePromptTemplate,
    Chain,
)


class LLMChainComponent(CustomComponent):
    display_name = "LLMChain"
    description = "Chain to run queries against LLMs"
    documentation: str = "https://docs.aiplanet.com/components/chains#llm-chain"

    def build_config(self):
        return {
            "prompt": {"display_name": "Prompt"},
            "llm": {"display_name": "LLM"},
            "memory": {"display_name": "Memory"},
            "code": {"show": False},
        }

    def build(
        self,
        prompt: BasePromptTemplate,
        llm: BaseLanguageModel,
        memory: Optional[BaseMemory] = None,
    ) -> Chain:
        return LLMChain(prompt=prompt, llm=llm, memory=memory)
