from genflow import CustomComponent
from langchain.embeddings.base import Embeddings
from langchain.embeddings import HuggingFaceInferenceAPIEmbeddings


class HuggingFaceInferenceAPIEmbeddingsComponent(CustomComponent):
    display_name: str = "HuggingFaceInferenceAPI Embeddings"
    description: str = """Access HuggingFaceEmbedding model via inference api, 
which does not require to install sentence_transformers 
and download models locally."""
    documentation: str = (
        "https://python.langchain.com/docs/integrations/text_embedding/huggingfacehub"
    )
    beta = False

    def build_config(self):
        return {
            "inference_api_key": {
                "display_name": "Inference API Key",
                "is_list": False,
                "required": True,
                "value": "",
            },
            "model_name": {
                "display_name": "Model Name",
                "is_list": False,
                "required": True,
                "value": "",
            },
            "code": {"show": False},
        }

    def build(self, inference_api_key: str, model_name: str) -> Embeddings:
        return HuggingFaceInferenceAPIEmbeddings(
            api_key=inference_api_key, model_name=model_name
        )

from typing import Optional, Union
from langchain.memory.chat_memory import BaseMemory, BaseChatMemory
from langchain.memory.buffer import ConversationBufferMemory
from langchain.memory.chat_message_histories.postgres import PostgresChatMessageHistory

from genflow import CustomComponent


class ConversationBufferMemoryComponent(CustomComponent):
    display_name: str = "ConversationBufferMemory"
    description: str = "Buffer for storing conversation memory."
    documentation: str = (
        "https://docs.aiplanet.com/components/memories#conversationbuffermemory"
    )
    beta = False

    def build_config(self):
        return {
            "input_key": {
                "display_name": "Input Key",
                "required": False,
                "value": "",
                "info": "The variable to be used as Chat Input when more than one variable is available.",
            },
            "memory_key": {
                "display_name": "Memory Key",
                "required": False,
                "value": "history",
            },
            "output_key": {
                "display_name": "Output Key",
                "required": False,
                "value": "",
                "info": "The variable to be used as Chat Output (e.g. answer in a ConversationalRetrievalChain)",
            },
            "return_messages": {
                "display_name": "Return Messages",
                "field_type": "bool",
                "required": False,
                "value": True,
            },
            "session_id": {
                "display_name": "Session ID",
                "required": False,
                "advanced": True,
                "value": "genflow_memory_db",
            },
            "connection_string": {
                "display_name": "Connection String",
                "required": False,
                "advanced": True,
                "value": "",
            },
            "code": {"show": False},
        }

    def build(
        self,
        input_key: Optional[str] = "",
        memory_key: Optional[str] = "history",
        output_key: Optional[str] = "",
        return_messages: Optional[bool] = True,
        session_id: Optional[str] = "genflow_memory_db",
        connection_string: Optional[str] = "",
    ) -> Union[BaseMemory, BaseChatMemory]:
        chat_memory = PostgresChatMessageHistory(
            session_id=session_id, connection_string=connection_string
        )

        keys = {}

        if input_key and input_key != "":
            keys["input_key"] = input_key

        if output_key and output_key != "":
            keys["output_key"] = output_key

        return ConversationBufferMemory(
            chat_memory=chat_memory,
            memory_key=memory_key,
            return_messages=return_messages,
            **keys
        )
