from typing import List
from langchain.retrievers import BM25Retriever, EnsembleRetriever
from langchain.schema import Document, BaseRetriever
from langchain.vectorstores.base import VectorStore

from genflow import CustomComponent


class EnsembleRetrieverComponent(CustomComponent):
    display_name: str = "Ensemble Retriever"
    description: str = "The EnsembleRetriever takes a list of retrievers as input."
    documentation: str = (
        "https://python.langchain.com/docs/modules/data_connection/retrievers/ensemble"
    )
    beta = False

    def build_config(self):
        return {
            "documents": {"display_name": "Documents"},
            "retrievers": {
                "display_name": "Retrievers",
            },
            "top_k": {
                "display_name": "Top K",
                "is_list": False,
                "required": True,
                "value": 2,
                "info": "The number of results to return.",
            },
            "weights": {
                "display_name": "Weights",
                "is_list": False,
                "required": True,
                "value": "0.5, 0.5",
            },
            "code": {"show": False},
        }

    def build(
        self,
        top_k: int,
        weights: str,
        documents: List[Document],
        retrievers: List[VectorStore],
    ) -> BaseRetriever:
        docs = [document.page_content for document in documents]
        bm25_retriever = BM25Retriever.from_texts(docs)
        bm25_retriever.k = top_k

        _retrievers = [
            _retriever.as_retriever(search_kwargs={"k": top_k})
            for _retriever in retrievers
        ]
        _retrievers.append(bm25_retriever)

        _weights = [
            int(_weight) if isinstance(_weight, int) else float(_weight)
            for _weight in weights.split(",")
        ]

        return EnsembleRetriever(retrievers=_retrievers, weights=_weights)

from typing import Optional
from genflow.interface.custom import CustomComponent
from langchain.llms.base import BaseLLM
from langchain.chat_models import AzureChatOpenAI


class AzureChatOpenAILLM(CustomComponent):
    display_name: str = "AzureChatOpenAI"
    description: str = "Azure Chat Open AI Chat&Completion large language models."

    AZURE_OPENAI_MODELS = [
        "gpt-4",
        "gpt-4-32k",
        "gpt-4-vision",
    ]

    def build_config(self):
        return {
            "model": {
                "display_name": "Model Name",
                "value": "gpt-4",
                "options": self.AZURE_OPENAI_MODELS,
                "required": True,
            },
            "api_key": {
                "display_name": "AzureChatOpenAI API Key",
                "required": True,
                "password": True,
            },
            "api_base": {
                "display_name": "AzureChatOpenAI API Base",
                "required": True,
            },
            "api_type": {"display_name": "AzureChatOpenAI API Type", "required": True},
            "azure_deployment": {
                "display_name": "Deployment Name",
                "required": True,
            },
            "api_version": {
                "display_name": "API Version",
                "value": "2023-07-01-preview",
                "required": True,
                "advanced": True,
            },
            "temperature": {
                "display_name": "Temperature",
                "value": 0.5,
                "field_type": "float",
                "required": False,
            },
            "max_tokens": {
                "display_name": "Max Tokens",
                "value": 512,
                "required": False,
                "field_type": "int",
                "advanced": True,
            },
            "code": {"show": False},
        }

    def build(
        self,
        model: str,
        api_base: str,
        api_type: str,
        api_key: str,
        azure_deployment: str,
        api_version: str = "2023-05-15",
        temperature: Optional[float] = 0.7,
        max_tokens: Optional[int] = 512,
    ) -> BaseLLM:
        try:
            output = AzureChatOpenAI(
                model_name=model,
                openai_api_base=api_base,
                openai_api_type=api_type,
                openai_api_key=api_key,
                openai_api_version=api_version,
                deployment_name=azure_deployment,
                temperature=temperature,
                max_tokens=max_tokens,
            )
        except Exception as e:
            raise ValueError("Could not connect to Azure ChatOpenAI model.") from e
        return output
