from genflow import CustomComponent
from genflow.field_typing import VectorStore, BaseRetriever


class VectorStoreRetrieverComponent(CustomComponent):
    display_name: str = "VectorStoreRetriever"
    description: str = "VectorStore retriever"
    beta = False
    version: str = "1.0"

    def build_config(self):
        return {
            "vector_store": {
                "display_name": "VectorStore",
                "field_type": "VectorStore",
            },
            "search_type": {
                "display_name": "Search Type",
                "value": "similarity",
                "options": ["similarity", "mmr"],
            },
            "search_kwargs": {
                "display_name": "Search Kwargs",
                "field_type": "dict",
                "value": [{"k": 4}],
            },
            "code": {"show": False},
        }

    def build(
        self,
        search_type: str,
        search_kwargs: dict,
        vector_store: VectorStore,
    ) -> BaseRetriever:
        return vector_store.as_retriever(
            search_type=search_type, search_kwargs=search_kwargs
        )

